const fs = require('fs');
const path = require('path');

const examplesDir = path.resolve(__dirname, '../static/examples');
const outputDir = path.resolve(__dirname, '../src/lib/generated');
const outputPath = path.join(outputDir, 'exampleSystems.ts');

try {
    const files = fs.readdirSync(examplesDir);
    const jsonFiles = files.filter(file => file.endsWith('.json'));

    const content = `// This file is auto-generated by scripts/generate-examples-list.js
// Do not edit this file directly.

export const exampleSystems: string[] = ${JSON.stringify(jsonFiles, null, 4)};
`;

    if (!fs.existsSync(outputDir)) {
        fs.mkdirSync(outputDir, { recursive: true });
    }

    fs.writeFileSync(outputPath, content);

    console.log(`Successfully generated ${outputPath}`);
} catch (error) {
    console.error('Error generating example systems list:', error);
    process.exit(1);
}
